import "./App.css";
import styled from "styled-components";
import React, { useState } from "react";
import {
  Button,
  Input,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  FormControlLabel,
  FormLabel,
  RadioGroup,
  Radio,
  CssBaseline,
  Container,
  Typography,
  Grid,
  Paper,
  TextField,
} from "@material-ui/core";

function App() {
  const citysArray = [
    {
      cityId: 1,
      cityName: "Minneapolis",
    },
    {
      cityId: 2,
      cityName: "Rochester",
    },
    {
      cityId: 3,
      cityName: "Bloomington",
    },
    {
      cityId: 4,
      cityName: "Duluth",
    },
    {
      cityId: 5,
      cityName: "Saint Paul",
    },
  ];
  const [name, setName] = useState("");
  const [selectedCity, setSelectedCity] = useState([]);
  const [email, setEmail] = useState("");
  const [notification, setNotification] = useState("");

  const handleNameOnChange = (event) => {
    setName(event.target.value);
  };

  const handleCityOnChange = (event) => {
    setSelectedCity(event.target.value);
  };

  const handleEmailOnChange = (event) => {
    setEmail(event.target.value);
  };

  const handleNotificationChange = () => {};

  return (
    <React.Fragment>
      <header className="App-header"></header>

      <Container maxWidth="sm">
        <form className={"simpleForm"} noValidate autoComplete="off">
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                error={false}
                required
                id="nameInput"
                label="Name"
                value={name}
                defaultValue=""
                helperText="Please enter your name"
                onChange={handleNameOnChange}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                id="citySelection"
                required
                select
                label="City"
                value={selectedCity}
                onChange={handleCityOnChange}
                helperText="Please select your city"
              >
                {citysArray.map((cityInfo) => (
                  <MenuItem key={cityInfo.cityId} value={cityInfo.cityName}>
                    {cityInfo.cityName}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>
            <Grid item xs={12}>
              <TextField
                error={false}
                required
                id="emailInput"
                label="Email"
                value={email}
                defaultValue=""
                helperText="Please enter your email"
                onChange={handleEmailOnChange}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl component="fieldset">
                <FormLabel component="legend">Gender</FormLabel>
                <RadioGroup
                  aria-label="ReciveNotification"
                  name="Recive notification"
                  value={value}
                  onChange={handleNotificationChange}
                >
                  <FormControlLabel
                    value="Yes"
                    control={<Radio />}
                    label="Yes"
                  />
                  <FormControlLabel value="No" control={<Radio />} label="No" />
                </RadioGroup>
              </FormControl>
            </Grid>
          </Grid>
        </form>
      </Container>
    </React.Fragment>
  );
}

export default App;
