import "./App.css";
import styled from "styled-components";
import React, { useState } from "react";
import {
  Button,
  Input,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  FormControlLabel,
  FormLabel,
  RadioGroup,
  Radio,
  CssBaseline,
  Container,
  Typography,
  Grid,
  Paper,
  TextField,
  FormHelperText,
} from "@material-ui/core";

function App() {
  const citysArray = [
    {
      cityId: 1,
      cityName: "Minneapolis",
    },
    {
      cityId: 2,
      cityName: "Rochester",
    },
    {
      cityId: 3,
      cityName: "Bloomington",
    },
    {
      cityId: 4,
      cityName: "Duluth",
    },
    {
      cityId: 5,
      cityName: "Saint Paul",
    },
  ];
  const [name, setName] = useState("");
  const [selectedCity, setSelectedCity] = useState([]);
  const [email, setEmail] = useState("");
  const [notification, setNotification] = useState(true);
  const [nameError, setNameError] = useState(false);
  const [selectedCityError, setSelectedCityError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [notificationError, setNotificationError] = useState(false);

  const handleNameOnChange = (event) => {
    event.preventDefault();
    event.stopPropagation();
    if (event.target.value !== "") {
      setNameError(false);
      setName(event.target.value);
    }
  };

  const handleCityOnChange = (event) => {
    event.preventDefault();
    event.stopPropagation();
    if (event.target.value !== "") {
      setSelectedCityError(false);
      setSelectedCity(event.target.value);
    }
  };

  const handleEmailOnChange = (event) => {
    event.preventDefault();
    event.stopPropagation();
    if (event.target.value !== "") {
      setEmailError(false);
      setEmail(event.target.value);
    }
  };

  const handleNotificationChange = (event) => {
    event.preventDefault();
    event.stopPropagation();
    if (event.target.value !== "") {
      setNotificationError(false);
      setNotification(event.target.value);
    }
  };

  const handleNameOnBlur = (event) => {
    event.preventDefault();
    event.stopPropagation();
    const hasError = !event.target.value ? true : false;
    setNameError(hasError);
  };

  const handleCityOnClose = (event) => {
    event.preventDefault();
    event.stopPropagation();
    const hasError = !event.target.value ? true : false;
    setSelectedCityError(hasError);
  };

  const handleEmailOnBlur = (event) => {
    event.preventDefault();
    event.stopPropagation();
    const hasError = !event.target.value ? true : false;
    setEmailError(hasError);
  };

  const handleNotificationOnBlur = (event) => {
    event.preventDefault();
    event.stopPropagation();
    const hasError = !event.target.value ? true : false;
    setNotificationError(hasError);
  };

  const formRest = () => {
    setName("");
    setSelectedCity("");
    setEmail("");
    setNotification("");
  };

  return (
    <React.Fragment>
      <header className="App-header">Sample Form</header>

      <Container maxWidth="sm">
        <form className={"simpleForm"} noValidate autoComplete="off">
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                error={nameError}
                required
                id="nameInput"
                label="Name"
                value={name}
                helperText="Please enter your name"
                onChange={handleNameOnChange}
                onBlur={handleNameOnBlur}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl
                required
                className={"citySelection"}
                error={selectedCityError}
              >
                <InputLabel id="cityLable">Age</InputLabel>
                <Select
                  labelId="cityLable"
                  id="citySelection"
                  value={selectedCity}
                  onChange={handleCityOnChange}
                  onClose={handleCityOnClose}
                >
                  {citysArray.map((cityInfo) => (
                    <MenuItem key={cityInfo.cityId} value={cityInfo.cityName}>
                      {cityInfo.cityName}
                    </MenuItem>
                  ))}
                </Select>
                <FormHelperText>Please select a city</FormHelperText>
              </FormControl>
            </Grid>
            <Grid item xs={12}>
              <TextField
                error={emailError}
                required
                id="emailInput"
                label="Email"
                value={email}
                helperText="Please enter your email"
                onChange={handleEmailOnChange}
                onBlur={handleEmailOnBlur}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl component="fieldset">
                <FormLabel component="legend">Gender</FormLabel>
                <RadioGroup
                  error={notificationError}
                  aria-label="ReciveNotification"
                  name="Recive notification"
                  value={notification}
                  onChange={handleNotificationChange}
                  onBlur={(event) => handleNotificationOnBlur(event)}
                >
                  <FormControlLabel
                    value="Yes"
                    control={<Radio />}
                    label="Yes"
                  />
                  <FormControlLabel value="No" control={<Radio />} label="No" />
                </RadioGroup>
              </FormControl>
            </Grid>
            <Grid item xs={6} sm={6} spacing={5}>
              <Button
                className={"resetBtn"}
                variant="contained"
                onClick={formRest}
              >
                Reset
              </Button>
              <Button
                className={"submitBtn"}
                variant="contained"
                color="primary"
              >
                Submit
              </Button>
            </Grid>
          </Grid>
        </form>
      </Container>
    </React.Fragment>
  );
}

export default App;
